mapper.select:
SELECT ROWNUM, O.CODE, M_NAME, M_DEPART, M_JOB, MA.MA_CODE, MA_NAME, O.ID, GUBUN, DIVISION, NAME, AP, OS, CPU, RAM, CAPACITY, BATTERY, GPU, PANNEL, HZ, RESOLUTION, SPEED, SHAPE
FROM OWN O
JOIN MEMBER MEM ON O.CODE = MEM.CODE
JOIN EQUIPMENTVIEW E ON O.ID = E.ID
JOIN MANUFACTURE MA ON E.M_CODE = MA.MA_CODE

	
		and (m_name like '%' || upper(#{m_name}) || '%' or m_name like '%' || lower(#{m_name}) || '%')
		and (ma_name like '%' || upper(#{ma_name}) || '%' or ma_name like '%' || lower(#{ma_name}) || '%')
		and (name like '%' || upper(#{name}) || '%' or name like '%' || lower(#{name}) || '%') 
		and (ap like '%' || upper(#{ap}) || '%' or ap like '%' || lower(#{ap}) || '%'  or ap like '%' || initcap(#{ap}) || '%')
		and (os like '%' || upper(#{os}) || '%' or os like '%' || lower(#{os}) || '%' or os like '%' || initcap(#{os}) || '%')
		and (cpu like '%' || upper(#{cpu}) || '%' or cpu like '%' || lower(#{cpu}) || '%'  or cpu like '%' || initcap(#{cpu}) || '%')
		and ram = #{ram} 
		and capacity = #{capacity} 
		and battery = #{battery} 
		and (gpu like '%' || upper(#{gpu}) || '%' or gpu like '%' || lower(#{gpu}) || '%' OR gpu like '%' || INITCAP(#{gpu}) || '%')
		and (pannel like '%' || upper(#{pannel}) || '%' or pannel like '%' || lower(#{pannel}) || '%') 
		and hz = #{hz} 
		and (resolution like '%' || upper(#{resolution}) || '%' or resolution like '%' || lower(#{resolution}) || '%') 
		and speed = #{speed} 
		
			and gubun in
			
				#{arr}
			
		
		and o.code = #{code} 
		and o.id = #{id}

select * from equipmentView e, member mem, manufacture ma
where 1=1
and e.code = mem.code and e.m_code = ma.ma_code
and e.code = #{code}
and e.m_code = #{m_code}
and id = upper(#{id})

SELECT * FROM EQUIPMENTVIEW

	
		and code = #{code}
		and gubun = #{gubun}
		and id = #{id}

mapper.update:
UPDATE OWN
SET CODE = #{owner}
WHERE ID = #{id} AND CODE = #{code}
