mapper.update:
update phone set
p_name = #{p_name}, p_ap = #{p_ap}, p_os = #{p_os}, p_cpu = #{p_cpu}, p_ram = #{p_ram}, p_capacity = #{p_capacity}, p_battery = #{p_battery}, m_code = #{m_code},
where p_id = #{id}

update pc set
pc_name = #{pc_name}, pc_gpu = #{pc_gpu}, pc_os = #{pc_os}, pc_cpu = #{pc_cpu}, pc_ram = #{pc_ram}, p_ram = #{p_ram}, pc_capacity = #{pc_capacity}, m_code = #{m_code},
where pc_id = #{id}

update monitor set
mo_name = #{mo_name}, mo_pannel = #{mo_pannel}, mo_hz = #{mo_hz}, mo_resolution = #{mo_resolution}, mo_speed = #{mo_speed}, mo_shape = #{mo_shape}, m_code = #{m_code},
where mo_id = #{id}
