mapper.select:
select * from equipmentView e, member mem, manufacture ma
where 1=1
and e.code = mem.code and e.m_code = ma.ma_code
and e.code = #{code}
and e.m_code = #{m_code}
and gubun = #{gubun}

mapper.update:
update phone set
p_name = #{p_name},
p_ap = #{p_ap}, p_os = #{p_os}, p_cpu = #{p_cpu}, p_ram = #{p_ram}, p_capacity = #{p_capacity}, p_battery = #{p_battery} where code = #{code}

update pc set
pc_name = #{p_name},
pc_gpu = #{p_gpu}, pc_os = #{p_os}, pc_cpu = #{p_cpu}, pc_ram = #{p_ram}, pc_capacity = #{p_capacity}
where code = #{code}

update monitor set
mo_name = #{mo_name},
mo_pannel = #{mo_pannel}, mo_hz = #{mo_hz}, mo_resolution = #{mo_resolution}, mo_speed = #{mo_speed}, mo_shape = #{mo_shape} where code = #{code}
